//weather
import React, { useState } from "react";
import axios from "axios";

function Weather() {
  const [weather, setWeather] = useState({
    temp_min: 0,
    temp_max: 0,
    description: null,
  });

  const [isUploaded, setIsUploaded] = useState(true)

  console.log("1 - ", isUploaded);
  

  if(isUploaded){
  axios
    .get(
      "https://api.openweathermap.org/data/2.5/weather?q=Yafo&appid=46bacd4218d40f0729b3e0ffa1d82a55"
    )
    .then((response) => {
      const new_temp_min = Math.round(response.data.main.temp_min - 273);
      const new_temp_max = Math.round(response.data.main.temp_max - 273);
      const newDescription = Math.round(response.data.weather.main);
      // console.log(new_temp_min);
      // console.log(new_temp_max);
      console.log(newDescription);

      setWeather({
        temp_min: new_temp_min,
        temp_max: new_temp_max,
        description: newDescription,
      });
    });
    console.log("2 - ", isUploaded);

    setIsUploaded(false);
    console.log("3 - ", isUploaded);

  }
  console.log("4 - ", isUploaded);


  return (
    <footer>
      <button>click</button>
      <h1>{weather.temp_min}</h1>
      <h1>{weather.temp_max}</h1>
      <h1>{weather.description}</h1>
    </footer>
  );
}

export default Weather;
